package com.sgcc.bg.yszx.controller;import java.util.HashMap;import java.util.List;import java.util.Map;import javax.servlet.http.HttpServletRequest;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import com.alibaba.fastjson.JSON;import com.alibaba.fastjson.serializer.SerializerFeature;import com.sgcc.bg.service.DataDictionaryService; /** * @author cyj * 演示中心---数据字典的查询 */@Controller@RequestMapping(value = "DataDictionary")public class DataDictionaryController {	@Autowired	private DataDictionaryService dataDictionaryService;	/**	 *  演示中心---数据字典的查询	 * @param	 * @param	 * @return	 */	@ResponseBody	@RequestMapping("/selectForDataDictionary")	public  String  selectForDataDictionary(HttpServletRequest request) {	Map<String, Object>  map= new  HashMap<String, Object>();		String pcode = request.getParameter("pcode" ) == null ? "" : request.getParameter("pcode").toString();  		 List<Map<String, String>>   dictData= dataDictionaryService.selectDictDataByPcode(pcode);	    if(dictData.isEmpty()){	    	map.put("data", "");	 	    map.put("success", "false");	 	    map.put("msg", "查询失败");	    }else{	    	map.put("dictData",  dictData);	 	    map.put("success", "ture");	 	    map.put("msg", "查询成功");	    }	   	    String jsonStr=JSON.toJSONStringWithDateFormat(map,"yyyy-MM-dd",SerializerFeature.WriteDateUseDateFormat);		return jsonStr;	}		}