package com.sgcc.bg.yszx.controller;import javax.servlet.http.HttpServletRequest;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import com.alibaba.fastjson.JSON;import com.sgcc.bg.common.ResultWarp;import com.sgcc.bg.service.DataDictionaryService; /** * @author cyj * 演示中心---数据字典的查询 */@Controller@RequestMapping(value = "DataDictionary")public class DataDictionaryController {	@Autowired	private DataDictionaryService dataDictionaryService;	/**	 *  演示中心---数据字典的查询	 * @param	 * @param	 * @return	 */	@ResponseBody	@RequestMapping("/selectForDataDictionary")	public String  selectForDataDictionary(HttpServletRequest request) {		ResultWarp rw =  null;		String pcode = request.getParameter("pcode" ) == null ? "" : request.getParameter("pcode").toString(); 		if("".equals(pcode)){			rw = new ResultWarp(ResultWarp.FAILED ,"请填写数据字典编码！"); 		}else{			String  data="";			try{				 data= dataDictionaryService.getDictDataJsonStr(pcode);			}catch(Exception e){				 rw = new ResultWarp(ResultWarp.FAILED ,"查询失败！"); 			}			if(data.length()>0){				rw = new ResultWarp(ResultWarp.SUCCESS ,"查询成功！");				rw.addData("dataDictionary", data);			}else{				rw = new ResultWarp(ResultWarp.FAILED ,"查询失败！"); 			}		}		 return JSON.toJSONString(rw);  	}		}