package com.sgcc.bg.yszx.controller;  import java.util.Map;import javax.servlet.http.HttpServletRequest;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody; import com.sgcc.bg.yszx.service.IdeaInfoService;  /** * @author cyj * 演示中心---数据字典的查询 */@Controller@RequestMapping(value = "IdeaInfo")public class IdeaInfoController {	@Autowired	private IdeaInfoService ideaInfoService;			@ResponseBody	@RequestMapping(value = "/addIdeaInfo", method = RequestMethod.POST)	public String addIdeaInfo(@RequestBody Map<String, Object> paramsMap){		String  rw=ideaInfoService.addIdeaInfo(paramsMap);		return rw;	}	/**	 *  演示中心---数据字典的查询	 * @param	 * @param	 * @return	 */ 	@ResponseBody	@RequestMapping("/selectForLeader")	public String  selectForLeader(HttpServletRequest request) {		String  rw=ideaInfoService.selectForLeader(request);		return rw;			}}