package com.sgcc.bg.yszx.controller;  import javax.servlet.http.HttpServletRequest;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody; import com.sgcc.bg.yszx.service.IdeaInfoService;  /** * @author cyj * 演示中心---数据字典的查询 */@Controller@RequestMapping(value = "IdeaInfo")public class IdeaInfoController {	@Autowired	private IdeaInfoService ideaInfoService;	/**	 *  演示中心---数据字典的查询	 * @param	 * @param	 * @return	 */	@ResponseBody	@RequestMapping("/addIdeaInfo")	public String  addIdeaInfo(HttpServletRequest request) {		String  rw=ideaInfoService.addIdeaInfo(request);		return rw;			}}