mapper.select:
SELECT * FROM( SELECT ROWNUM rn,p.* FROM( SELECT t1.id as "id", t1.project_name as "projectName", (SELECT name FROM BG_SYS_DATADICTIONARY WHERE pcode='category100002' and vaild=1 and code=t1.category) as "category", t1.wbs_number as "WBSNumber", t1.project_number as "projectNumber", t1.project_introduce as "projectIntroduce", t1.organ_info as "organInfo", to_char(t1.start_date,'yyyy-mm-dd') as "startDate", to_char(t1.end_date,'yyyy-mm-dd') as "endDate", t1.plan_hours as "planHours", t1.project_status as "projectStatus", t1.decompose as "decompose", t1.status as "status", (select count(distinct t2.hrcode) from BG_PROJECT_USER t2 where t2.project_id=t1.id and t2.status='1') as "amount", (select t3.empname from BG_PROJECT_USER t3 WHERE t3.project_id=t1.id AND t3.role='1' and t3.status='1') as "principal", (select TO_CHAR(WM_CONCAT(distinct t3.empname)) from BG_PROJECT_USER t3 where t3.project_id=t1.id AND t3.status='1') as "empName" FROM BG_PROJECT_INFO t1 WHERE (#{username} IN (SELECT username FROM BG_SYS_USER t5 JOIN BG_PROJECT_USER t6 ON t5.hrcode = t6.hrcode WHERE t6.project_id = t1.id AND t6.role = '1' AND t6.status = '1') OR #{username}=t1.create_user)
AND t1.PROJECT_NAME LIKE '%'||#{proName}||'%'
AND t1.PROJECT_STATUS=#{proStatus}
AND t1.status='1' ORDER BY t1.UPDATE_DATE DESC) p WHERE ROWNUM<=#{end}) WHERE rn>#{start}

mapper.update:
UPDATE BG_PROJECT_INFO 
SET STATUS='0',UPDATE_USER=#{updateUser,jdbcType=VARCHAR},UPDATE_DATE=#{updateDate,jdbcType=TIMESTAMP}
WHERE ID=#{proId} AND status='1'

UPDATE BG_PROJECT_INFO 
SET PROJECT_STATUS=#{proStatus}
WHERE ID=#{proId} AND status='1'

