mapper.update:
UPDATE BG_PROJECT_INFO 
SET STATUS='0',UPDATE_USER=#{updateUser,jdbcType=VARCHAR},UPDATE_DATE=#{updateDate,jdbcType=TIMESTAMP}
WHERE ID=#{proId} AND status='1'

UPDATE BG_PROJECT_INFO 
SET PROJECT_STATUS=#{proStatus}
WHERE ID=#{proId} AND status='1'

mapper.select:
SELECT 


		TO_CHAR(${field},'yyyy-mm-dd')


		${field}


FROM BG_PROJECT_INFO  WHERE id=#{proId}

mapper.update:
UPDATE BG_PROJECT_USER T
   SET T.ROLE = #{role}
 WHERE T.PROJECT_ID = #{projectId}
   AND T.STATUS = '1'

	 AND T.HRCODE = #{in_hrCode}


	 AND T.HRCODE != #{ex_hrCode}

mapper.select:
SELECT T.ID, T.CATEGORY, T.PROJECT_ID, T.PROJECT_NAME, T.WBS_NUMBER, T.PROJECT_INTRODUCE, T.START_DATE, T.END_DATE FROM BG_SYNC_KY_PROJECT T JOIN BG_SYNC_KY_EMP E ON T.PROJECT_ID = E.PROJECT_ID AND E.VALID = '1' JOIN BG_SYS_USER U ON E.HRCODE = U.HRCODE WHERE T.VALID = '1' AND NOT EXISTS (SELECT 1 FROM BG_SYNC_PROJECT_RELATION R WHERE R.SYNC_ID = T.PROJECT_ID AND R.VALID = '1') AND U.USERNAME = #{username} AND E.ROLE = '1'
AND T.PROJECT_NAME LIKE '%'||#{proName}||'%'
AND T.WBS_NUMBER = #{wbsNumber}

SELECT T.ID, T.CATEGORY, T.PROJECT_ID, T.PROJECT_NAME, T.WBS_NUMBER, T.PROJECT_INTRODUCE, T.START_DATE, T.END_DATE FROM BG_SYNC_HX_PROJECT T JOIN BG_SYNC_HX_EMP E ON T.PROJECT_ID = E.PROJECT_ID AND E.VALID = '1' JOIN BG_SYS_USER U ON E.HRCODE = U.HRCODE WHERE T.VALID = '1' AND NOT EXISTS (SELECT 1 FROM BG_SYNC_PROJECT_RELATION R WHERE R.SYNC_ID = T.PROJECT_ID AND R.VALID = '1') AND U.USERNAME = #{username} AND E.ROLE = '1'
AND T.PROJECT_NAME LIKE '%'||#{proName}||'%'
AND T.WBS_NUMBER = #{wbsNumber}

mapper.delete:
DELETE FROM BG_SYNC_EMP_RELATION 
 WHERE BG_PROJECT_ID = #{proId}

	AND HRCODE = #{hrCode}

mapper.select:
SELECT T.BG_ID, T.SYNC_ID, T.SRC
  FROM BG_SYNC_PROJECT_RELATION T
 WHERE T.VALID = '1'

	AND T.BG_ID = #{proId}


	AND T.SYNC_ID = #{srcProId}


	AND T.SRC = #{src}

SELECT nvl(sum(WORKING_HOUR),0) as hourSum from BG_WORKINGHOUR_INFO
WHERE PROJECT_ID = #{id}
AND VALID = '1'

SELECT W.WORKING_HOUR, W.PROJECT_ID, W.APPROVER, W.WORKER, U.USERALIAS, TO_CHAR(W.WORK_TIME_BEGIN, 'yyyy-mm-dd') AS "WORK_TIME_BEGIN", TO_CHAR(W.WORK_TIME_END, 'yyyy-mm-dd') AS "WORK_TIME_END" FROM BG_WORKINGHOUR_INFO W left join BG_SYS_USER U ON W.WORKER = U.USERNAME WHERE W.VALID = '1' AND U.VALID = '1'
AND U.HRCODE= #{hrCode}
AND W.PROJECT_ID = #{proId}
AND W.WORK_TIME_BEGIN >= TO_DATE(#{start}, 'yyyy-mm-dd')
AND W.WORK_TIME_END <= TO_DATE(#{end}, 'yyyy-mm-dd')
