mapper.update:
UPDATE BG_KQ_EMP_TEMPORARY SET
STATUS =#{status}
WHERE  STATUS='1' and  ONCECODE=#{oncecode}

UPDATE BG_KQ_EMP_INFO SET
VALID =#{status}
WHERE  VALID=#{valid}
AND   to_char(BEGIN_DATE,'yyyy-mm-dd') >= #{beginDate}
AND   to_char(END_DATE,'yyyy-mm-dd') <= #{endDate}

mapper.delete:
DELETE   FROM  BG_KQ_EMP_INFO
WHERE   VALID = #{valid}
AND   to_char(BEGIN_DATE,'yyyy-mm-dd') >= #{beginDate}
AND   to_char(END_DATE,'yyyy-mm-dd') <= #{endDate}

mapper.select:
select K.UUID, to_char(K.BEGIN_DATE,'yyyy-mm-dd') BEGIN_DATE, to_char(K.END_DATE,'yyyy-mm-dd') END_DATE, K.EMP_CODE, U.USERALIAS AS EMP_NAME, K.FULL_TIME, K.OVER_TIME, to_char(K.CREATE_TIME,'yyyy-mm-dd hh24:mi:ss') CREATE_TIME, K.VALID from BG_KQ_EMP_INFO K left join BG_SYS_USER U ON K.EMP_CODE = U.HRCODE where K.VALID='1' AND U.VALID='1'
AND to_char(K.BEGIN_DATE,'yyyy-mm-dd') >= #{beginDate}
AND to_char(K.END_DATE,'yyyy-mm-dd') <= #{endDate}
and K.EMP_NAME LIKE '%'||#{empName}||'%'
and K.UUID in
#{id}
