mapper.update:
UPDATE BG_KQ_EMP_TEMPORARY SET
STATUS =#{status}
WHERE  STATUS='1' and  ONCECODE=#{oncecode}

UPDATE BG_KQ_EMP_INFO SET
VALID =#{status}
WHERE  VALID=#{valid}
AND   to_char(BEGIN_DATE,'yyyy-mm-dd') >= #{beginDate}
AND   to_char(END_DATE,'yyyy-mm-dd') >= #{endDate}

mapper.delete:
DELETE   FROM  BG_KQ_EMP_INFO
WHERE   VALID = #{valid}
AND   to_char(BEGIN_DATE,'yyyy-mm-dd') >= #{beginDate}
AND   to_char(END_DATE,'yyyy-mm-dd') >= #{endDate}

mapper.select:
select UUID, to_char(BEGIN_DATE,'yyyy-mm-dd') BEGIN_DATE, to_char(END_DATE,'yyyy-mm-dd') END_DATE, EMP_CODE, EMP_NAME, FULL_TIME, OVER_TIME, to_char(CREATE_TIME,'yyyy-mm-dd hh24:mi:ss') CREATE_TIME, VALID from BG_KQ_EMP_INFO where VALID='1'
AND to_char(BEGIN_DATE,'yyyy-mm-dd') <= #{beginDate}
AND to_char(END_DATE,'yyyy-mm-dd') >= #{endDate}
and EMP_NAME = #{empName}
and UUID in
#{id}
