mapper.select:
SELECT DISTINCT U.USERALIAS, U.USERNAME, U.HRCODE, B.ROLE_NAME, B.ROLE_CODE, DECODE(B.ROLE_CODE, 'MANAGER_UNIT', (SELECT DEPTCODE FROM BG_SYS_DEPT WHERE TYPE = 0), 'MANAGER_KJB', (SELECT DEPTCODE FROM BG_SYS_DEPT WHERE TYPE = 0), T.DEPTCODE) DEPTCODE, DECODE(B.ROLE_CODE, 'MANAGER_UNIT', (SELECT DEPTNAME FROM BG_SYS_DEPT WHERE TYPE = 0), 'MANAGER_KJB', (SELECT DEPTNAME FROM BG_SYS_DEPT WHERE TYPE = 0), T.DEPTNAME) DEPTNAME, DECODE(B.ROLE_CODE, 'MANAGER_UNIT', 0, 'MANAGER_KJB', 0, T.TYPE) TYPE FROM BG_SYS_PRIV_USER_R_ROLE A JOIN BG_SYS_ROLE B ON A.ROLE_ID = B.ID AND B.VALID = 1 JOIN BG_SYS_USER U ON U.USERID = A.USER_ID AND U.STATE = 0 LEFT JOIN (SELECT O.USER_ID, D.DEPTCODE, D.DEPTNAME, D.TYPE FROM BG_SYS_PRIV_USER_R_ORGAN O JOIN BG_SYS_DEPT D ON O.ORGAN_ID = D.DEPTID AND D.VALID = 1 AND O.VALID = 1) T ON A.USER_ID = T.USER_ID AND DECODE(B.ROLE_CODE, 'MANAGER_UNIT', 0, 'MANAGER_KJB', 0, 'MANAGER_DEPT', 1, 'MANAGER_LAB', '2') = T.TYPE WHERE A.VALID = 1
AND U.USERALIAS LIKE '%'||#{username}||'%'
AND T.DEPTCODE = #{deptCode}
AND B.ROLE_CODE = #{roleCode}
ORDER BY DEPTNAME NULLS LAST

mapper.delete:

		UPDATE BG_SYS_PRIV_USER_R_ORGAN O
  		  	SET O.VALID = 0
		 WHERE 1 = 1

				AND (SELECT PDEPTCODE FROM BG_SYS_DEPT WHERE DEPTID = O.ORGAN_ID) = #{pDeptCode}


				AND (SELECT HRCODE FROM BG_SYS_USER WHERE USERID = O.USER_ID) = #{empCode}
