mapper.select:
SELECT b.ID, (SELECT name FROM BG_SYS_DATADICTIONARY WHERE pcode='category100002' and vaild=1 and code=NVL(p.CATEGORY,b.CATEGORY)) as CATEGORY , NVL(p.PROJECT_NAME, b.PROJECT_NAME) as PROJECT_NAME, b.PROJECT_ID, b.JOB_CONTENT, b.WORKING_HOUR, b.APPROVER as APPROVER, b.WORKER, b.DEPT_ID, c.DEPTNAME, b.LAB_ID, D.DEPTNAME LABNAME, b.STATUS, b.VALID, b.CREATE_USER, b.UPDATE_USER, T.USERALIAS, T.HRCODE, R.PROCESS_NOTE, (SELECT name FROM BG_SYS_DATADICTIONARY WHERE pcode='cstatus100003' and vaild=1 and code=b.STATUS) as PROSTATUS, TO_CHAR (b.CREATE_TIME, 'yyyy-MM-dd') AS CREATE_TIME, TO_CHAR (b.UPDATE_TIME, 'yyyy-MM-dd') AS UPDATE_TIME, TO_CHAR (b.WORK_TIME, 'yyyy-MM-dd') AS WORK_TIME FROM BG_WORKINGHOUR_INFO b LEFT JOIN BG_PROJECT_INFO p ON p.ID=b.PROJECT_ID LEFT JOIN BG_SYS_USER T ON T .USERNAME = b.WORKER LEFT JOIN BG_SYS_DEPT c ON c.deptid = b.DEPT_ID AND c.valid = 1 LEFT JOIN BG_SYS_DEPT D ON b.LAB_ID = D.deptid AND D.valid = 1 LEFT JOIN BG_PROCESS_RECORD R ON R.ID = B.PROCESS_ID AND R.VALID = 1 WHERE b.VALID = 1 and b.approver = #{hrName} and (b.status = '2' or b.status = '3')
and b.WORK_TIME >= TO_DATE(#{startTime}, 'yyyy-mm-dd hh24:mi:ss')
and b.WORK_TIME <= TO_DATE(#{endTime}, 'yyyy-mm-dd hh24:mi:ss')
and NVL(p.PROJECT_NAME, b.PROJECT_NAME) like '%'||#{projectName}||'%'
and NVL(p.CATEGORY,b.CATEGORY) = #{type}
and T.USERALIAS = #{userName}
and T.HRCODE = #{userCode}
AND b.id IN
#{id123,jdbcType=VARCHAR}
order by b.CREATE_TIME desc

select rownum as ROW_ID, b.ID, b.PROJECT_NAME, (SELECT name FROM BG_SYS_DATADICTIONARY WHERE pcode='category100002' and vaild=1 and code=b.CATEGORY) as CATEGORY , b.PROJECT_NUMBER, b.WBS_NUMBER, b.PROJECT_INTRODUCE, b.ORGAN_INFO, to_char(b.START_DATE, 'yyyy-MM-dd') as START_DATE, to_char(b.END_DATE, 'yyyy-MM-dd') as END_DATE, F.ID as userId, b.PLAN_HOURS, b.PROJECT_STATUS, b.DECOMPOSE, b.STATUS, (SELECT name FROM BG_SYS_DATADICTIONARY WHERE pcode='pstatus100001' and vaild=1 and code=b.PROJECT_STATUS) as proType, b.CREATE_USER, to_char(F.START_DATE, 'yyyy-MM-dd') as personStart, to_char(F.END_DATE, 'yyyy-MM-dd') as personEnd, b.UPDATE_USER, (select t4.useralias from BG_PROJECT_USER t3 JOIN BG_SYS_USER t4 ON t3.hrcode = t4.hrcode WHERE t3.project_id = b.id AND t3.role = 1) as principal, to_char(b.UPDATE_DATE, 'yyyy-MM-dd') as UPDATE_DATE, to_char(b.CREATE_DATE, 'yyyy-MM-dd') as CREATE_DATE from BG_PROJECT_INFO b LEFT JOIN BG_PROJECT_USER F ON F.PROJECT_ID = b.id where b.ID in (select t.PROJECT_ID from BG_PROJECT_USER t where t.HRCODE = #{hrCode}) and F.HRCODE = #{hrCode}
and b.PROJECT_NAME like '%'||#{projectName}||'%'
and b.category = #{type}
and (TO_DATE(#{startTime}, 'yyyy-mm-dd hh24:mi:ss') BETWEEN F.START_DATE and F.END_DATE or TO_DATE(#{endTime}, 'yyyy-mm-dd hh24:mi:ss') BETWEEN F.START_DATE and F.END_DATE or (to_char(F.START_DATE,'yyyy-mm-dd') >= #{startTime} and to_char(F.END_DATE,'yyyy-mm-dd') <= #{endTime} ))
AND F.id IN
#{id123,jdbcType=VARCHAR}
