mapper.select:
SELECT * FROM (SELECT ROWNUM rn, p.* FROM (SELECT t.id, to_char(t.work_time,'yyyy-mm-dd') work_time, (SELECT deptname FROM BG_SYS_DEPT WHERE deptid = t.dept_id) as dept, (SELECT deptname FROM BG_SYS_DEPT WHERE deptid = t.lab_id) as lab, u.useralias as username, u.hrcode, DECODE(t.category, 'NP', t.project_name, p.project_name) as project_name, DECODE(t.category, 'NP', '非项目工作', (SELECT name FROM BG_SYS_DATADICTIONARY WHERE pcode='category100002' and vaild=1 and code=p.category)) as category, t.job_content, t.working_hour, t.approver as approver_username, a.useralias as approver, t.status FROM BG_WORKINGHOUR_INFO t LEFT JOIN BG_SYS_USER u ON u.username=t.worker LEFT JOIN BG_SYS_USER a ON a.username=t.approver LEFT JOIN BG_PROJECT_INFO p ON p.id=t.project_id WHERE t.valid=1 AND (SELECT R.DEPTID FROM BG_SYS_USER_DEPT_RELATION R WHERE R.USERID = u.userid AND trunc(sysdate) BETWEEN R.EXP_START_TIME AND R.EXP_END_TIME AND ((R.SRC = 0 AND NOT EXISTS (SELECT R1.USERID FROM BG_SYS_USER_DEPT_RELATION R1 WHERE R1.USERID = R.USERID AND trunc(sysdate) BETWEEN R1.EXP_START_TIME AND R1.EXP_END_TIME AND R1.VALID = 1 AND R1.SRC = 1)) OR R.SRC IN (1, 2)) AND R.VALID = 1 AND ROWNUM = 1) IN (${deptIds})
AND t.work_time >= TO_DATE(#{startDate}, 'yyyy-mm-dd')
AND t.work_time <= TO_DATE(#{endDate}, 'yyyy-mm-dd')
AND DECODE(t.category,'NP',t.category,p.category) = #{category}
AND DECODE(t.category, 'NP', t.project_name, p.project_name) LIKE '%'||#{proName}||'%'
AND u.useralias LIKE '%'||#{empName}||'%'
AND t.status=#{status}
ORDER BY t.work_time,t.create_time DESC) p WHERE ROWNUM<=#{end}) WHERE rn>#{start}

mapper.update:
UPDATE BG_WORKINGHOUR_INFO SET status = #{status,jdbcType=VARCHAR}
	WHERE valid=1

		AND id = #{id,jdbcType=VARCHAR}
