mapper.select:
SELECT U.USERID, U.USERNAME, U.HRCODE, U.USERALIAS, D.PDEPTID, D2.DEPTNAME PDEPTNAME, D.DEPTID, D.DEPTNAME, D.PDEPTCODE, D.DEPTCODE, U.STATE, U.MOBILE, U.EMAIL, D.TYPE FROM BG_SYS_USER_DEPT_RELATION R INNER JOIN BG_SYS_USER U ON U.USERID = R.USERID INNER JOIN BG_SYS_DEPT D ON D.DEPTID = R.DEPTID INNER JOIN BG_SYS_DEPT D2 ON D.PDEPTID = D2.DEPTID WHERE 1 = 1
AND U.USERNAME = #{userName,jdbcType=VARCHAR}
AND U.HRCODE = #{hrCode,jdbcType=VARCHAR}
AND TO_DATE(#{curDate,jdbcType=VARCHAR},'yyyy-MM-dd') BETWEEN R.EXP_START_TIME AND R.EXP_END_TIME AND ((R.SRC = 0 AND NOT EXISTS (SELECT R1.USERID FROM BG_SYS_USER_DEPT_RELATION R1 WHERE R1.USERID = R.USERID AND TO_DATE(#{curDate,jdbcType=VARCHAR},'yyyy-MM-dd') BETWEEN R1.EXP_START_TIME AND R1.EXP_END_TIME AND R1.VALID = 1 AND R1.SRC = 1)) OR R.SRC IN (1,2)) AND R.VALID = 1 AND ROWNUM = 1
