mapper.select:
SELECT W.ID
  FROM BG_WORKINGHOUR_INFO W
  LEFT JOIN BG_SYS_USER U
    ON U.USERNAME = W.WORKER
WHERE 1 = 1		  
  AND W.STATUS = '3'
  AND W.VALID = 1
  AND U.HRCODE IS NOT NULL


		AND 1=2


		AND  TO_CHAR(W.WORK_TIME_BEGIN, 'yyyy') = #{yearName, jdbcType = VARCHAR}
		AND  TO_CHAR(W.WORK_TIME_END, 'yyyy') = #{yearName, jdbcType = VARCHAR}


			and   TO_CHAR(W.WORK_TIME_BEGIN,'yyyy-mm-dd') >= #{startDate, jdbcType = VARCHAR}
			and   TO_CHAR(W.WORK_TIME_END,'yyyy-mm-dd') <= #{endDate, jdbcType = VARCHAR}


		AND  TO_CHAR(W.WORK_TIME_BEGIN, 'yyyy-MM') = #{monthName, jdbcType = VARCHAR}
		AND  TO_CHAR(W.WORK_TIME_END, 'yyyy-MM') = #{monthName, jdbcType = VARCHAR}


		AND 1=2

mapper.sql:
SELECT W.ID, W.WORK_TIME, U.HRCODE EMP_CODE, U.USERNAME, U.USERALIAS, P.ID AS PROJECT_ID, DECODE(P.CATEGORY, NULL,'0', '', '0', 1) WT_TYPE, P.PROJECT_NUMBER WBS_CODE, P.PROJECT_NAME WBS_NAME, NVL(P.CATEGORY,W.CATEGORY) as CATEGORY, NVL(P.PROJECT_NAME, W.PROJECT_NAME) as PROJECT_NAME, (CASE WHEN P.ID IS NOT NULL AND PU2.HRCODE = U.HRCODE THEN '1' WHEN P.ID IS NOT NULL AND PU2.HRCODE != U.HRCODE THEN '0' ELSE NULL END) ROLE, (CASE WHEN P.ID IS NOT NULL AND PU2.HRCODE = U.HRCODE THEN '1' WHEN P.ID IS NOT NULL AND PU2.HRCODE != U.HRCODE THEN '0' ELSE NULL END) PROJECT_ROLE, U1.HRCODE PROJECT_LEADER, U1.USERALIAS LEADER_USERNAME, NVL(W.WORKING_HOUR,0) WORKING_HOUR, W.APPROVER as APPROVER FROM BG_WORKINGHOUR_INFO W LEFT JOIN BG_SYS_USER U ON U.USERNAME = W.WORKER LEFT JOIN BG_PROJECT_INFO BP ON BP.ID = W.PROJECT_ID LEFT JOIN BG_PROJECT_INFO P ON P.ID = BP.RELATED_PROJECT_ID LEFT JOIN BG_PROJECT_USER PU1 ON W.PROJECT_ID = PU1.PROJECT_ID AND PU1.HRCODE = U.HRCODE AND ROWNUM = 1 LEFT JOIN BG_PROJECT_USER PU2 ON P.ID = PU2.PROJECT_ID AND PU2.ROLE = '1' LEFT JOIN BG_SYS_USER U1 ON U1.HRCODE = PU2.HRCODE AND ROWNUM = 1 WHERE 1 = 1 AND W.CATEGORY = 'BP' AND W.STATUS = '3' AND W.VALID = 1 AND U.HRCODE IS NOT NULL
AND 1=2
AND TO_CHAR(W.WORK_TIME_BEGIN, 'yyyy') = #{yearName, jdbcType = VARCHAR} AND TO_CHAR(W.WORK_TIME_END, 'yyyy') = #{yearName, jdbcType = VARCHAR}
and TO_CHAR(W.WORK_TIME_BEGIN,'yyyy-mm-dd') >= #{startDate, jdbcType = VARCHAR} and TO_CHAR(W.WORK_TIME_END,'yyyy-mm-dd') <= #{endDate, jdbcType = VARCHAR}
AND TO_CHAR(W.WORK_TIME_BEGIN, 'yyyy-MM') = #{monthName, jdbcType = VARCHAR} AND TO_CHAR(W.WORK_TIME_END, 'yyyy-MM') = #{monthName, jdbcType = VARCHAR}
AND 1=2

SELECT W.ID, W.WORK_TIME, U.HRCODE EMP_CODE, U.USERNAME, U.USERALIAS, P.ID PROJECT_ID, DECODE(P.CATEGORY, NULL,'0', '', '0', 1) WT_TYPE, P.PROJECT_NUMBER WBS_CODE, P.PROJECT_NAME WBS_NAME, NVL(P.CATEGORY,W.CATEGORY) as CATEGORY, NVL(P.PROJECT_NAME, W.PROJECT_NAME) as PROJECT_NAME, PU1.ROLE, DECODE(PU1.ROLE, NULL, NULL, '', NULL, '1', '1', '0', '0') PROJECT_ROLE, U1.HRCODE PROJECT_LEADER, U1.USERALIAS LEADER_USERNAME, NVL(W.WORKING_HOUR,0) WORKING_HOUR, W.APPROVER as APPROVER FROM BG_WORKINGHOUR_INFO W LEFT JOIN BG_SYS_USER U ON U.USERNAME = W.WORKER LEFT JOIN BG_PROJECT_INFO P ON P.ID = W.PROJECT_ID LEFT JOIN BG_PROJECT_USER PU1 ON W.PROJECT_ID = PU1.PROJECT_ID AND PU1.HRCODE = U.HRCODE AND ROWNUM = 1 LEFT JOIN BG_PROJECT_USER PU2 ON W.PROJECT_ID = PU2.PROJECT_ID AND PU2.ROLE = '1' LEFT JOIN BG_SYS_USER U1 ON U1.HRCODE = PU2.HRCODE AND ROWNUM = 1 WHERE 1 = 1 AND W.CATEGORY != 'BP' AND U.HRCODE IS NOT NULL AND W.STATUS = '3' AND W.VALID = 1
AND 1=2
AND TO_CHAR(W.WORK_TIME_BEGIN, 'yyyy') = #{yearName, jdbcType = VARCHAR} AND TO_CHAR(W.WORK_TIME_END, 'yyyy') = #{yearName, jdbcType = VARCHAR}
and TO_CHAR(W.WORK_TIME_BEGIN,'yyyy-mm-dd') >= #{startDate, jdbcType = VARCHAR} and TO_CHAR(W.WORK_TIME_END,'yyyy-mm-dd') <= #{endDate, jdbcType = VARCHAR}
AND TO_CHAR(W.WORK_TIME_BEGIN, 'yyyy-MM') = #{monthName, jdbcType = VARCHAR} AND TO_CHAR(W.WORK_TIME_END, 'yyyy-MM') = #{monthName, jdbcType = VARCHAR}
AND 1=2
UNION ALL SELECT NULL ID, NULL WORK_TIME, U.HRCODE EMP_CODE, U2.USERNAME USERNAME, U2.USERALIAS USERALIAS, P.ID PROJECT_ID, DECODE(P.CATEGORY, NULL, '0', '', '0', '1') WT_TYPE, P.PROJECT_NUMBER WBS_CODE, P.PROJECT_NAME WBS_NAME, P.CATEGORY CATEGORY, P.PROJECT_NAME PROJECT_NAME, U.ROLE ROLE, DECODE(U.ROLE, NULL, NULL, '', NULL, '1', '1', '0', '0') PROJECT_ROLE, U.HRCODE PROJECT_LEADER, U2.USERALIAS LEADER_USERNAME, 0 WORKING_HOUR, U2.USERNAME APPROVER FROM (SELECT DISTINCT U.HRCODE, U.PROJECT_ID, U.ROLE FROM BG_PROJECT_USER U INNER JOIN BG_PROJECT_INFO P ON P.ID = U.PROJECT_ID AND P.STATUS = 1 WHERE U.STATUS = 1 AND U.ROLE = '1' AND (EXISTS ( SELECT W.ID FROM BG_WORKINGHOUR_INFO W WHERE 1 = 1 AND W.PROJECT_ID = U.PROJECT_ID AND W.STATUS = '3' AND W.CATEGORY IN ('JS','KY','HX','QT')
AND 1=2
AND TO_CHAR(W.WORK_TIME_BEGIN, 'yyyy') = #{yearName, jdbcType = VARCHAR} AND TO_CHAR(W.WORK_TIME_END, 'yyyy') = #{yearName, jdbcType = VARCHAR}
and TO_CHAR(W.WORK_TIME_BEGIN,'yyyy-mm-dd') >= #{startDate, jdbcType = VARCHAR} and TO_CHAR(W.WORK_TIME_END,'yyyy-mm-dd') <= #{endDate, jdbcType = VARCHAR}
AND TO_CHAR(W.WORK_TIME_BEGIN, 'yyyy-MM') = #{monthName, jdbcType = VARCHAR} AND TO_CHAR(W.WORK_TIME_END, 'yyyy-MM') = #{monthName, jdbcType = VARCHAR}
AND 1=2
AND W.VALID = 1) OR EXISTS ( SELECT W.ID FROM BG_WORKINGHOUR_INFO W INNER JOIN BG_PROJECT_INFO BP ON BP.ID = W.PROJECT_ID WHERE 1 = 1 AND W.CATEGORY ='BP' AND BP.RELATED_PROJECT_ID = U.PROJECT_ID AND W.STATUS = '3'
AND 1=2
AND TO_CHAR(W.WORK_TIME_BEGIN, 'yyyy') = #{yearName, jdbcType = VARCHAR} AND TO_CHAR(W.WORK_TIME_END, 'yyyy') = #{yearName, jdbcType = VARCHAR}
and TO_CHAR(W.WORK_TIME_BEGIN,'yyyy-mm-dd') >= #{startDate, jdbcType = VARCHAR} and TO_CHAR(W.WORK_TIME_END,'yyyy-mm-dd') <= #{endDate, jdbcType = VARCHAR}
AND TO_CHAR(W.WORK_TIME_BEGIN, 'yyyy-MM') = #{monthName, jdbcType = VARCHAR} AND TO_CHAR(W.WORK_TIME_END, 'yyyy-MM') = #{monthName, jdbcType = VARCHAR}
AND 1=2
AND W.VALID = 1 )) AND (NOT EXISTS ( SELECT W.ID FROM BG_WORKINGHOUR_INFO W INNER JOIN BG_SYS_USER SU ON SU.USERNAME = W.WORKER WHERE 1 = 1 AND W.CATEGORY IN ('JS', 'KY', 'HX', 'QT') AND W.PROJECT_ID = U.PROJECT_ID AND SU.HRCODE = U.HRCODE AND W.STATUS = '3'
AND 1=2
AND TO_CHAR(W.WORK_TIME_BEGIN, 'yyyy') = #{yearName, jdbcType = VARCHAR} AND TO_CHAR(W.WORK_TIME_END, 'yyyy') = #{yearName, jdbcType = VARCHAR}
and TO_CHAR(W.WORK_TIME_BEGIN,'yyyy-mm-dd') >= #{startDate, jdbcType = VARCHAR} and TO_CHAR(W.WORK_TIME_END,'yyyy-mm-dd') <= #{endDate, jdbcType = VARCHAR}
AND TO_CHAR(W.WORK_TIME_BEGIN, 'yyyy-MM') = #{monthName, jdbcType = VARCHAR} AND TO_CHAR(W.WORK_TIME_END, 'yyyy-MM') = #{monthName, jdbcType = VARCHAR}
AND 1=2
AND W.VALID = 1 ) OR NOT EXISTS (SELECT W.ID FROM BG_WORKINGHOUR_INFO W INNER JOIN BG_SYS_USER SU ON SU.USERNAME = W.WORKER INNER JOIN BG_PROJECT_INFO BP ON BP.ID = W.PROJECT_ID WHERE 1 = 1 AND W.CATEGORY = 'BP' AND BP.RELATED_PROJECT_ID = U.PROJECT_ID AND SU.HRCODE = U.HRCODE AND W.STATUS = '3'
AND 1=2
AND TO_CHAR(W.WORK_TIME_BEGIN, 'yyyy') = #{yearName, jdbcType = VARCHAR} AND TO_CHAR(W.WORK_TIME_END, 'yyyy') = #{yearName, jdbcType = VARCHAR}
and TO_CHAR(W.WORK_TIME_BEGIN,'yyyy-mm-dd') >= #{startDate, jdbcType = VARCHAR} and TO_CHAR(W.WORK_TIME_END,'yyyy-mm-dd') <= #{endDate, jdbcType = VARCHAR}
AND TO_CHAR(W.WORK_TIME_BEGIN, 'yyyy-MM') = #{monthName, jdbcType = VARCHAR} AND TO_CHAR(W.WORK_TIME_END, 'yyyy-MM') = #{monthName, jdbcType = VARCHAR}
AND 1=2
AND W.VALID = 1))) U INNER JOIN BG_PROJECT_INFO P ON P.ID = U.PROJECT_ID AND P.STATUS = 1 INNER JOIN BG_SYS_USER U2 ON U2.HRCODE = U.HRCODE
