mapper.update:
UPDATE TB_YSZX_IDEA SET
	VALID=#{valId,jdbcType=VARCHAR},
	UPDATE_USER=#{updateUser,jdbcType=VARCHAR},
	UPDATE_TIME=#{updateTime,jdbcType=TIMESTAMP}
WHERE UUID=#{ideaId}

UPDATE TB_YSZX_VISIT SET
	VALID=#{valId,jdbcType=VARCHAR},
	UPDATE_USER=#{updateUser,jdbcType=VARCHAR},
	UPDATE_TIME=#{updateTime,jdbcType=TIMESTAMP}
WHERE UUID=#{visitId}

		UPDATE TB_YSZX_COMPANY_LEADER SET
			VALID=#{valId,jdbcType=VARCHAR},
			UPDATE_USER=#{updateUser,jdbcType=VARCHAR},
			UPDATE_TIME=#{updateTime,jdbcType=TIMESTAMP}
		WHERE  VALID='1'
		   
			      and  USERID = #{userid,jdbcType=VARCHAR} 
		   
		    
			       and   IDEAID = #{ideaId,jdbcType=VARCHAR}

UPDATE TB_YSZX_COMPANY_USER SET
	VALID=#{valId,jdbcType=VARCHAR},
	UPDATE_USER=#{updateUser,jdbcType=VARCHAR},
	UPDATE_TIME=#{updateTime,jdbcType=TIMESTAMP}
WHERE UUID=#{companyId}

mapper.select:
SELECT CODE as "code", NAME as "name", SORT_ID as "sortId" FROM BG_SYS_DATADICTIONARY WHERE VAILD = '1' AND PCODE = #{pcode,jdbcType=VARCHAR} AND CODE IN
#{code,jdbcType=VARCHAR}
ORDER BY SORT_ID

SELECT CODE as "code", NAME as "name", SORT_ID as "sortId" FROM BG_SYS_DATADICTIONARY WHERE VAILD = '1' AND PCODE = #{pcode,jdbcType=VARCHAR}
and CODE like '%'||#{code}||'%'
ORDER BY SORT_ID
