mapper.update:
update bg_lw_grade
set score = #{score},
update_user = #{updateUser},
update_time = sysdate
where pme_id = #{pmeId}
and rule_id = #{ruleId}
and valid = #{valid}

mapper.select:
select * from(
select p.*, ROWNUM as r
from (select p.uuid paperUuid,
    m.uuid pmeId,
    p.paper_id paperId,
    p.paper_name paperName,
    p.year,
    p.unit,
    p.author,
    p.journal,
    p.recommend_unit recommendUnit,
    p.paper_type paperType,
    p.quote_count quoteCount,
    p.download_count downloadCount,
    p.field,
    m.score,
    (SELECT name
    FROM BG_SYS_DATADICTIONARY
    WHERE pcode = 'score_status'
    and vaild = 1
    and code = m.score_status) as scoreStatus,
    m.score_status scoreStatusCode,
    ROWNUM rown
    from    bg_lw_paper      p，
    bg_lw_paper_match_expert m,
    bg_lw_specialist s
    where 1 = 1

        and p.paper_name = #{paperName}


        and p.year = #{year}


        and p.score_status = #{scoreStatus}


        and p.paper_type = #{paperType}

    and p.score_table_status = ${scoreTableStatus}
    and s.user_id = #{userId}
    and p.uuid = m.paper_id
    and m.specialist_id = s.uuid
    and p.valid = #{valid}
    and m.valid = #{valid}
    and s.valid = #{valid}
    order by p.paper_id desc
) p
where  #{pageEnd} >= ROWNUM
) where r > #{pageStart}

select count(*) from (select p.uuid, p.paper_id paperId, p.paper_name paperName, p.year, p.unit, p.author, p.journal, p.recommend_unit recommendUnit, p.paper_type paperType, p.quote_count quoteCount, p.download_count downloadCount, p.field, m.score, (SELECT name FROM BG_SYS_DATADICTIONARY WHERE pcode = 'score_status' and vaild = 1 and code = m.score_status) as scoreStatus, ROWNUM rown from bg_lw_paper p， bg_lw_paper_match_expert m, bg_lw_specialist s where 1 = 1
and p.paper_name = #{paperName}
and p.year = #{year}
and p.score_status = #{scoreStatus}
and p.paper_type = #{paperType}
and p.score_table_status = ${scoreTableStatus} and s.user_id = #{userId} and p.uuid = m.paper_id and m.specialist_id = s.uuid and p.valid = #{valid} and m.valid = #{valid} and s.valid = #{valid} order by p.paper_id desc ) p

select s.uuid,
    f.first_index,
    s.second_index,
    s.weights sweights,
    s.REFERENCE_REQUIRE require,
    f.weights fweights

        ,g.score

from bg_lw_rule_first f,
    bg_lw_rule_second s

        ,bg_lw_grade g

where 1 = 1
    and f.uuid = s.first_index_id
    and f.year = #{year}
    and f.paper_type = #{paperType}
    and f.valid = #{valid}
    and s.valid = #{valid}

        and g.rule_id = s.uuid
        and g.pme_id = #{pmeId}

    order by first_order, second_order

select s.nums
from bg_lw_rule_first f,
    (select s.first_index_id, count(s.first_index_id) nums
    from bg_lw_rule_second s
    where s.valid = #{valid}
    group by s.first_index_id) s
where s.first_index_id = f.uuid
    and f.valid = #{valid}
    and f.year = #{year}
    and f.paper_type = #{paperType}
order by f.first_order
