mapper.update:
update bg_lw_paper
set  SCORE_TABLE_STATUS  = #{scoreTableStatus}
where UUID  = #{uuid}

update bg_lw_paper
set  SCORE_STATUS  = #{scoreStatus}
where UUID  = #{uuid}

update bg_lw_paper
set  ALL_STATUS  = #{allStatus}
where UUID  = #{uuid}

update bg_lw_paper
set  VALID  = #{valid}
where UUID  = #{uuid}

mapper.select:
select p.uuid uuid, p.paper_id paperId, p.paper_name paperName, p.year, p.unit, p.author, p.journal, p.recommend_unit recommendUnit, p.paper_type paperType, p.quote_count quoteCount, p.download_count downloadCount, p.field, p.score_status as scoreStatus, p.score_table_status as scoreTableStatus, p.all_status as allStatus, (SELECT name FROM BG_SYS_DATADICTIONARY WHERE pcode='score_status' and vaild=1 and code= p.score_status) as scoreStatusDetail, (SELECT name FROM BG_SYS_DATADICTIONARY WHERE pcode='score_table_status' and vaild=1 and code= p.score_table_status) as scoreTableStatusDetail, (SELECT name FROM BG_SYS_DATADICTIONARY WHERE pcode='paper_all_status' and vaild=1 and code= p.all_status) as allStatusDetail, TO_CHAR(p.create_time, 'YYYY-MM-DD HH24:MI') createTime, TO_CHAR(p.update_time, 'YYYY-MM-DD HH24:MI') updateTime, p.valid from bg_lw_paper p where 1=1
and p.uuid = #{uuid}
and p.paper_name = #{paperName}
and p.valid = #{valid}

select * from(
select p.*,ROWNUM as r
from (select p.uuid,
    p.paper_id paperId,
    p.paper_name paperName,
    p.year,
    p.unit,
    p.author,
    p.journal,
    p.recommend_unit recommendUnit,
    p.paper_type paperType,
    p.quote_count quoteCount,
    p.download_count downloadCount,
    p.field,
    p.score_table_status scoreTableStatus,
    (SELECT name FROM BG_SYS_DATADICTIONARY  WHERE pcode='score_status' and vaild=1 and code= p.score_status)  as scoreStatus,
    p.all_status allStatus,
    p.create_user createUser,
    p.update_user updateUser,
    TO_CHAR(p.create_time, 'YYYY-MM-DD HH24:MI') createTime,
    TO_CHAR(p.update_time, 'YYYY-MM-DD HH24:MI') updateTime,
    p.valid,
    p.WEIGHTING_FRACTION weightingFaction,
    p.AVERAGE_FRACTION averageFaction,
    ROWNUM rown
    from bg_lw_paper p
    where 1 = 1

      and p.paper_name = #{paperName}


      and p.year = #{year}


      and p.paper_id = #{paperId}


      and p.unit = #{unit}


      and p.author = #{author}


      and p.field = #{field}


        and p.score_status = #{scoreStatus}


        and p.paper_type = #{paperType}

    and valid = #{valid}
    order  by p.paper_id desc
) p
where  #{pageEnd} >= ROWNUM
) where r > #{pageStart}

select count(*)
from bg_lw_paper p
where 1 = 1

    and p.paper_name = #{paperName}


    and p.year = #{year}


    and p.paper_id = #{paperId}


    and p.unit = #{unit}


    and p.author = #{author}


    and p.field = #{field}


    and p.score_status = #{scoreStatus}


    and p.paper_type = #{paperType}

and p.valid = #{valid}

SELECT a.* FROM ( SELECT p.UUID AS uuid, p.PAPER_ID AS paperId, p.PAPER_NAME AS paperName, p.YEAR AS year, p.UNIT AS unit, p.AUTHOR AS author, p.JOURNAL AS journal, p.RECOMMEND_UNIT AS recommendUnit, p.PAPER_TYPE AS paperType, p.QUOTE_COUNT AS quoteCount, p.DOWNLOAD_COUNT AS downloadCount, p.FIELD AS field, p.SCORE_TABLE_STATUS AS scoreTableStatus, p.SCORE_STATUS AS scoreStatus, p.ALL_STATUS AS allStatus, p.WEIGHTING_FRACTION AS weightingFraction, p.AVERAGE_FRACTION AS averageFraction, ROWNUM AS ROWNO FROM bg_lw_paper p WHERE p.valid = '1' AND p.ALL_STATUS = '6'
and p.YEAR = #{year,jdbcType=VARCHAR}
and p.AUTHOR = #{author,jdbcType=VARCHAR}
and p.PAPER_ID = #{paperId,jdbcType=VARCHAR}
and p.PAPER_NAME = #{paperName,jdbcType=VARCHAR}
AND #{end} >= ROWNUM ORDER BY P.AVERAGE_FRACTION DESC nulls last ) a WHERE a.ROWNO > #{start}

SELECT COUNT(UUID) FROM bg_lw_paper
WHERE valid = '1'
AND ALL_STATUS = '6'

    and YEAR = #{year,jdbcType=VARCHAR}


    and AUTHOR = #{author,jdbcType=VARCHAR}


    and PAPER_ID = #{paperId,jdbcType=VARCHAR}


    and PAPER_NAME = #{paperName,jdbcType=VARCHAR}

SELECT p.UUID AS uuid, p.PAPER_ID AS paperId, p.PAPER_NAME AS paperName, p.YEAR AS year, p.UNIT AS unit, p.AUTHOR AS author, p.JOURNAL AS journal, p.RECOMMEND_UNIT AS recommendUnit, p.PAPER_TYPE AS paperType, p.QUOTE_COUNT AS quoteCount, p.DOWNLOAD_COUNT AS downloadCount, p.FIELD AS field, p.SCORE_TABLE_STATUS AS scoreTableStatus, p.SCORE_STATUS AS scoreStatus, p.ALL_STATUS AS allStatus, p.WEIGHTING_FRACTION AS weightingFraction, p.AVERAGE_FRACTION AS averageFraction, ROWNUM AS ROWNO FROM bg_lw_paper p WHERE p.valid = '1' AND p.ALL_STATUS = '6'
and p.YEAR = #{year,jdbcType=VARCHAR}
and p.AUTHOR = #{author,jdbcType=VARCHAR}
and p.PAPER_ID = #{paperId,jdbcType=VARCHAR}
and p.PAPER_NAME = #{paperName,jdbcType=VARCHAR}
ORDER BY P.AVERAGE_FRACTION DESC nulls last

