select p.uuid uuid, p.paper_id paperId, p.paper_name paperName, p.year, p.unit, p.author, p.journal, p.recommend_unit recommendUnit, (SELECT name FROM BG_SYS_DATADICTIONARY WHERE pcode='paper_type' and vaild=1 and code= p.paper_type) as paperType, p.quote_count quoteCount, p.download_count downloadCount, p.field, (SELECT name FROM BG_SYS_DATADICTIONARY WHERE pcode='score_status' and vaild=1 and code= p.score_status) as scoreStatus, p.valid from bg_lw_paper p where 1=1 AND p.uuid IN (
#{uuid}
)

select p.uuid,
    p.paper_id paperId,
    p.paper_name paperName,
    p.year,
    p.unit,
    p.author,
    p.journal,
    p.recommend_unit recommendUnit,
    (SELECT name FROM BG_SYS_DATADICTIONARY  WHERE pcode='paper_type' and vaild=1 and code= p.paper_type)  as paperType,
    p.quote_count quoteCount,
    p.download_count downloadCount,
    p.field,
    p.WEIGHTING_FRACTION weightingFaction,
    p.AVERAGE_FRACTION averageFaction
from bg_lw_paper p
where 1 = 1

    and p.paper_name = #{paperName}


    and p.year = #{year}


    and p.paper_id = #{paperId}


    and p.unit = #{unit}


    and p.author = #{author}


    and p.field = #{field}


    and p.score_status = #{scoreStatus}


    and p.paper_type = #{paperType}

and valid = #{valid}
order  by p.paper_id desc

select
    field,
    research_direction researchDirection,
    unit_name unitName,
    name,
    uuid
from bg_lw_specialist where valid = #{valid}

    and (
            research_direction LIKE '%'||#{field}||'%'
            or
            field LIKE '%'||#{field}||'%'
        )


    and name not in (

            #{author}

    )


  and unit_name != #{unit}

SELECT a.* FROM ( SELECT p.UUID AS uuid, p.PAPER_ID AS paperId, p.PAPER_NAME AS paperName, p.WEIGHTING_FRACTION AS weightingFraction, p.AVERAGE_FRACTION AS averageFraction, ROWNUM AS ROWNO FROM bg_lw_paper p WHERE p.valid = '1' AND p.ALL_STATUS = '6'
and p.year = #{year,jdbcType=VARCHAR}
and p.paper_name LIKE '%'||#{paperName,jdbcType=VARCHAR}||'%'
and p.paper_id = #{paperId,jdbcType=VARCHAR}
and p.field = #{field}
AND #{end} >= ROWNUM ORDER BY P.AVERAGE_FRACTION DESC nulls last ) a WHERE a.ROWNO > #{start}

