mapper.update:
update bg_lw_paper_match_expert set
socre = #{score,jdbcType=VARCHAR},
update_user = #{updateUser,jdbcType=VARCHAR},
update_time = #{updateTime,jdbcType=TIMESTAMP}
where 1=1

      and paper_id= #{paperId}


      and specialist_id = #{specialistId}

update bg_lw_paper_match_expert set
socre_status = #{socreStatus,jdbcType=VARCHAR}
  where 1=1

      and paper_id= #{paperId}


      and specialist_id = #{specialistId}

update bg_lw_paper_match_expert set valid = #{valid}
where 1=1

    and paper_id= #{paperId}


    and specialist_id = #{specialistId}

mapper.select:
select
    uuid,
    paper_id paperId,
    specialist_id specialistId,
    score,
    score_status scoreStatus
from bg_lw_paper_match_expert where 1=1

  and paper_id= #{paperId}


  and specialist_id = #{specialistId}

and valid = #{valid}

select s.field, s.unit_name unitName, s.name, m.specialist_id uuid from bg_lw_paper_match_expert m , bg_lw_specialist s where m.specialist_id = s.uuid
and m.paper_id= #{paperId}
and m.valid = #{valid} order by m.SPECIALIST_SORT

mapper.update:
UPDATE BG_LW_PAPER_MATCH_EXPERT
SET VALID = '0'
WHERE SPECIALIST_ID = #{beforeUuid,jdbcType=VARCHAR}
