mapper.sql:
SELECT * FROM ( SELECT Z1.PSPID , Z1.POSID , Z1.PTIME , Z1.POST1, Z1.PRART1 , Z1.SPST1, ROUND(DECODE(Z1.ZGSZTZ,NUll,0,Z1.ZGSZTZ)/10000, 2)ZGSZTZ, ROUND(DECODE(Z1.WERT1,NUll,0,Z1.WERT1)/10000, 2)WERT1, Z1.ZZJLY , Z1.ZZJLY_T, Z1.ZZJXZ, Z1.ZZJXZ_T, Z1.PRCTR, Z1.KTEXT , Z1.PLFAZ , Z1.PLSEZ , ROUND(DECODE(Z1.ZSQJE,NUll,0, Z1.ZSQJE)/10000, 2)ZSQJE, ROUND(DECODE(Z1.ZDDJE,NUll,0, Z1.ZDDJE)/10000, 2)ZDDJE, ROUND(DECODE(Z1.ZFPRZ,NUll,0, Z1.ZFPRZ)/10000, 2)ZFPRZ, Z2.GJAHR, ROUND(DECODE(Z2.ZJFZCE,NUll,0, Z2.ZJFZCE)/10000, 2)ZJFZCE, ER.SPECIAL_COMPANY_CODE, SC1.SPECIAL_NAME as SPECIAL_COMPANY_NAME, FS.FUNDS_SOURCE_NAME, FS.SPECIAL_EPRI_CODE, SC.SPECIAL_NAME, DECODE(MP.IMAGE_PROGRESS, NULL,'0.00', '', '0.00', MP.IMAGE_PROGRESS ) IMAGE_PROGRESS , DECODE(MP.EXECUTION_PROGRESS, NULL,'0.00', '', '0.00', MP.EXECUTION_PROGRESS ) EXECUTION_PROGRESS , DECODE(MP.BIDDING_PROGRESS, NULL,'0.00', '', '0.00', MP.BIDDING_PROGRESS) BIDDING_PROGRESS, DECODE(MP.SYSTEM_DEV_PROGRESS, NULL,'0.00', '', '0.00', MP.SYSTEM_DEV_PROGRESS) SYSTEM_DEV_PROGRESS, DECODE(MP.PLANNED_COMPLETION, NULL,'0', '', '0', MP.PLANNED_COMPLETION) PLANNED_COMPLETION FROM PS_ERP_PROJECT_INFO Z1 LEFT JOIN PS_ERP_PROJECT_SPEND Z2 ON Z1.PSPID=Z2.PSPID and Z1.PTIME=Z2.GJAHR LEFT JOIN PS_SPECIAL_ERP_RELATION ER ON ER.ERP_SPECIAL_CODE=Z1.PRART1 LEFT JOIN PS_FUNDS_SOURCE FS ON FS.FUNDS_SOURCE_CODE=Z1.ZZJLY and FS.SPECIAL_COMPANY_CODE=ER.SPECIAL_COMPANY_CODE LEFT JOIN PS_SPECIAL_CATEGORY SC ON SC.SPECIAL_CODE=FS.SPECIAL_EPRI_CODE LEFT JOIN PS_SPECIAL_CATEGORY SC1 ON SC1.SPECIAL_CODE=ER.SPECIAL_COMPANY_CODE LEFT JOIN PS_MAINTAIN_PROJECT MP ON MP.PROJECT_ID =Z1.PSPID and MP.YEAR=Z1.PTIME and MP.VALID='1' where 1=1 UNION ALL SELECT MY.ID as PSPID, null POSID, MY.YEAR as PTIME, null POST1, null PRART1, SC.SPECIAL_NAME SPST1, null ZGSZTZ, TO_NUMBER(MY.PLAN_AMOUNT) as WERT1, null ZZJLY, null ZZJLY_T, DECODE(MY.SPECIAL_TYPE, 'C013','00001', 'C014', '00001', 'C012', '00002' , '' ) as ZZJXZ, DECODE(MY.SPECIAL_TYPE, 'C013','资本化', 'C014', '资本化', 'C012', '成本化', '' ) as ZZJXZ_T, MY.BEARER as PRCTR, PC.PROFIT_CENTER_DEATIL as KTEXT, null PLFAZ, null PLSEZ , null ZSQJE , null ZDDJE , null ZFPRZ , null GJAHR, null ZJFZCE, MY.SPECIAL_TYPE as SPECIAL_COMPANY_CODE, SC.SPECIAL_NAME as SPECIAL_COMPANY_NAME, null FUNDS_SOURCE_NAME, null SPECIAL_EPRI_CODE, SC.SPECIAL_NAME, MY.IMAGE_PROGRESS, '0.00' EXECUTION_PROGRESS, '0.00' BIDDING_PROGRESS, '0.00' SYSTEM_DEV_PROGRESS, MY.ITEM_NUMBER PLANNED_COMPLETION FROM PS_MAINTAIN_YEAR MY LEFT JOIN PS_SPECIAL_CATEGORY SC ON MY.SPECIAL_TYPE=SC.SPECIAL_CODE LEFT JOIN ( SELECT DISTINCT PROFIT_CENTER_CODE, PROFIT_CENTER_DEATIL FROM PS_PROFIT_CENTER where 1=1) PC ON PC.PROFIT_CENTER_CODE=MY.BEARER where MY.VALID='1' ) where 1=1
and PTIME = #{maintainMap.year}
and SPECIAL_COMPANY_CODE = #{maintainMap.specialType}
and PRCTR= #{maintainMap.commitmentUnit}
and PSPID= #{maintainMap.projectCode}
and ZZJXZ= #{maintainMap.projectType}
AND SPECIAL_EPRI_CODE IN
#{specialCode,jdbcType=VARCHAR}
and ZZJLY IN
#{sourceOfFund,jdbcType=VARCHAR}
ORDER BY PTIME DESC

