mapper.sql:
SELECT ID, PROJECT_ID, NODE_SORT, NODE_NAME, IMAGE_PROGRESS, SPECAIL_TYPE, CREATE_USER, CREATE_TIME, UPDATA_USER, UPDATA_TIME, YEAR FROM PS_MAINRTAIN_NODE WHERE VALID='1'
and PROJECT_ID = #{nodeMap.projectId}
and NODE_NAME = #{nodeMap.nodeName}
and YEAR = #{nodeMap.year}
and ID = #{nodeMap.id}
and ID != #{nodeMap.idF}
ORDER BY to_number(NODE_SORT)

mapper.select:
SELECT  T.*  from(
SELECT  TB.* , ROWNUM AS ROWNO   from(

) TB  WHERE #{nodeMap.page_end} >= ROWNUM
) T   WHERE T.ROWNO > #{nodeMap.page_start}

SELECT N.ID, N.PROJECT_ID, N.NODE_SORT, N.NODE_NAME, N.IMAGE_PROGRESS, N.SPECAIL_TYPE, N.CREATE_USER, N.CREATE_TIME, N.UPDATA_USER, N.UPDATA_TIME, N.YEAR, Z1.POST1 AS PROJECTNAME FROM PS_MAINRTAIN_NODE N LEFT JOIN PS_ERP_PROJECT_INFO Z1 ON N.PROJECT_ID=Z1.PSPID WHERE VALID='1'
and N.PROJECT_ID = #{nodeMap.projectId}
and N.ID = #{nodeMap.id}
and N.YEAR = #{nodeMap.year}
and N.NODE_SORT = #{nodeMap.nodeSort}
ORDER BY to_number(N.IMAGE_PROGRESS) DESC

