mapper.select:
select * from (select p.*, ROWNUM as r from (select a.uuid, a.apply_code, a.use_seal_reason, d.deptname, u.useralias, TO_CHAR(a.use_seal_date, 'YYYY-MM-DD') use_seal_date, TO_CHAR(a.create_time, 'YYYY-MM-DD') create_time, s.second_category_name useSealItem, (select name from bg_sys_datadictionary where pcode = 'use_seal_status' and code = a.use_seal_status) use_seal_status, a.use_seal_status use_seal_status_code, (select replace(wm_concat(nvl(d.name, k.use_seal_value)),',',';') useSealKind from bg_yy_kind k, (select * from bg_sys_datadictionary d where d.pcode = 'use_seal_kind' and d.vaild = '1') d where k.use_seal_kind_code = d.code(+) and k.apply_id = a.uuid and k.valid='1') use_seal_kind from bg_yy_apply a left join bg_sys_dept d on d.deptId = a.dept_id left join bg_sys_user u on a.apply_user_id = u.userid left join bg_yy_item_second s on a.item_second_id = s.uuid left join bg_yy_item_first f on a.item_first_id = f.uuid where 1=1
and a.apply_code like '%'||#{applyCode}||'%'
and a.create_time >= to_date(#{startTime},'YYYY-MM-DD hh24:mi:ss')
and a.create_time <= to_date(#{endTime},'YYYY-MM-DD hh24:mi:ss')
and a.use_seal_status = #{useSealStatus}
and f.uuid = #{useSealItemFirst}
and s.uuid = #{itemSecondId}
and a.use_seal_reason like '%'||#{useSealReason}||'%'
and a.apply_user_id = #{userId} and a.valid = '1' order by a.create_time desc) p where #{pageEnd} >= ROWNUM) where r > #{pageStart}

select count(*) from bg_yy_apply a left join bg_sys_dept d on d.deptId = a.dept_id left join bg_sys_user u on a.apply_user_id = u.userid left join bg_yy_item_second s on a.item_second_id = s.uuid left join bg_yy_item_first f on a.item_first_id = f.uuid where 1=1
and a.apply_code like '%'||#{applyCode}||'%'
and a.create_time >= to_date(#{startTime},'YYYY-MM-DD hh24:mi:ss')
and a.create_time <= to_date(#{endTime},'YYYY-MM-DD hh24:mi:ss')
and a.use_seal_status = #{useSealStatus}
and f.uuid = #{useSealItemFirst}
and s.uuid = #{itemSecondId}
and a.use_seal_reason like '%'||#{useSealReason}||'%'
and a.apply_user_id = #{userId} and a.valid = '1' order by a.create_time desc

select a.uuid, a.apply_code applyCode, a.use_seal_reason useSealReason, a.dept_id applyDeptId, d.deptname applyDept, u.useralias applyUser, a.apply_user_id applyUserId, TO_CHAR(a.use_seal_date, 'YYYY-MM-DD') useSealDate, TO_CHAR(a.create_time, 'YYYY-MM-DD') createTime, f.first_category_name||'-'||s.second_category_name useSealItem, (select name from bg_sys_datadictionary where pcode = 'use_seal_status' and code = a.use_seal_status) useSealStatusValue, a.use_seal_status useSealStatus, (select replace(wm_concat(nvl(d.name, k.use_seal_value)),',',';') useSealKind from bg_yy_kind k, (select * from bg_sys_datadictionary d where d.pcode = 'use_seal_kind' and d.vaild = '1') d where k.use_seal_kind_code = d.code(+) and k.apply_id = a.uuid and k.valid='1') useSealKind from bg_yy_apply a left join bg_sys_dept d on d.deptId = a.dept_id left join bg_sys_user u on a.apply_user_id = u.userid left join bg_yy_item_second s on a.item_second_id = s.uuid left join bg_yy_item_first f on a.item_first_id = f.uuid where 1=1
and a.apply_code like '%'||#{applyCode}||'%'
and a.create_time >= to_date(#{startTime},'YYYY-MM-DD hh24:mi:ss')
and a.create_time <= to_date(#{endTime},'YYYY-MM-DD hh24:mi:ss')
and a.use_seal_status = #{useSealStatus}
and s.uuid = #{itemSecondId}
and a.use_seal_reason like '%'||#{useSealReason}||'%'
AND a.uuid IN (
#{uuid}
)
and a.apply_user_id = #{userId} and a.valid = '1' order by a.create_time desc
