mapper.sql:
SELECT * FROM ( SELECT S.FIRST_CATEGORY_ID as "firstCategoryId", F.FIRST_CATEGORY_NAME as "firstCategoryName", S.UUID as "secondCategoryId", S.SECOND_CATEGORY_NAME as "secondCategoryName", DECODE( S.IF_SIGN,'0','否','1','是') as "ifSign", S.IF_SIGN as "ifSignCode", TO_CHAR( (select wm_concat(NVL(D.DEPTNAME ,'')) FROM BG_YY_ITEM_SECOND_DEPT SD LEFT JOIN BG_SYS_DEPT D ON SD.DEPT_ID=D.DEPTID where SD. VALID ='1' AND SD.SECOND_CATEGORY_ID = S.UUID ) ) as "businessDeptName", TO_CHAR( (select wm_concat (NVL(SD.DEPT_ID, '')) FROM BG_YY_ITEM_SECOND_DEPT SD WHERE SD.VALID = '1' AND SD.SECOND_CATEGORY_ID = S.UUID ) )AS "businessDeptCode", DECODE(S.IF_LEADER_APPROVE,'0','否','1','是') as "ifLeaderApprove", S.IF_LEADER_APPROVE as "ifLeaderApproveCode", S.CREATE_TIME as "createTime" FROM BG_YY_ITEM_SECOND S LEFT JOIN BG_YY_ITEM_FIRST F ON S.FIRST_CATEGORY_ID=F.UUID WHERE S.VALID='1' and S.VALID='1' ) T where 1=1
and T."firstCategoryId" = #{mattersMap.itemFirst}
and T."secondCategoryId" = #{mattersMap.itemSecond}
and T."secondCategoryName" = #{mattersMap.itemSecondName}
and T."secondCategoryId" != #{mattersMap.uuid}
and T."businessDeptName" like '%'||#{mattersMap.businessDeptName}||'%'
and T."secondCategoryId" != #{mattersMap.uuid}
ORDER BY T."createTime" DESC

mapper.select:
SELECT  T.*  from(
SELECT  TB.* , ROWNUM AS ROWNO  from(

) TB  WHERE #{mattersMap.page_end} >= ROWNUM
) T   WHERE T.ROWNO > #{mattersMap.page_start}

mapper.sql:
SELECT      UUID     as  "firstCategoryId",
             FIRST_CATEGORY_NAME as "firstCategoryName",
             SORT_NUMBER as "sortNumber",
             VALID
FROM    BG_YY_ITEM_FIRST
WHERE   VALID='1'

     and   UUID = #{FirstInfoMap.itemFirst}


     and   FIRST_CATEGORY_NAME = #{FirstInfoMap.itemFirstName}


     and   UUID  != #{FirstInfoMap.uuid}

 ORDER BY SORT_NUMBER DESC

