mapper.sql:
select a.uuid, a.apply_code applyCode, a.use_seal_reason useSealReason, d.deptname applyDept, u.useralias applyUser, TO_CHAR(a.use_seal_date, 'YYYY-MM-DD') useSealDate, TO_CHAR(a.create_time, 'YYYY-MM-DD') createTime, f.first_category_name||'-'||s.second_category_name useSealItem, (select name from bg_sys_datadictionary where pcode = 'use_seal_status' and code = a.use_seal_status) use_seal_status, a.use_seal_status use_seal_status_code, (select replace(wm_concat(nvl(d.name, k.use_seal_value)),',',';') useSealKind from bg_yy_kind k, (select * from bg_sys_datadictionary d where d.pcode = 'use_seal_kind' and d.vaild = '1') d where k.use_seal_kind_code = d.code(+) and k.apply_id = a.uuid and k.valid='1') useSealKind from bg_yy_apply a left join bg_sys_dept d on d.deptId = a.dept_id left join bg_sys_user u on a.apply_user_id = u.userid left join bg_yy_item_second s on a.item_second_id = s.uuid left join bg_yy_item_first f on a.item_first_id = f.uuid where 1=1
and a.apply_code like '%'||#{applyCode}||'%'
and a.dept_id = #{deptId}
and u.USERALIAS like '%'||#{useSealUser}||'%'
AND a.UUID IN (SELECT DISTINCT BUSINESS_ID FROM TB_WL_BUSINESS_R_APPLY WHERE APPLY_ID IN ( SELECT apply_id FROM TB_WL_APPROVE WHERE id IN ( SELECT ID APPROVE_ID FROM TB_WL_APPROVE WHERE id IN ( SELECT APPROVE_ID FROM TB_WL_AUDIT_USER WHERE approve_user = #{loginUserId} AND valid = '1' AND APPROVE_EXPAND_ID IS NULL ) AND VALID = '1' AND AUDIT_FLAG = #{ifComingSoon}
AND (APPROVE_RESULT = '0' OR APPROVE_RESULT = '1')
UNION SELECT APPROVE_ID FROM TB_WL_APPROVE_EXPAND WHERE UUID IN (SELECT APPROVE_EXPAND_ID FROM TB_WL_AUDIT_USER WHERE APPROVE_USER = #{loginUserId} AND valid = '1' AND APPROVE_EXPAND_ID IS NOT NULL ) AND VALID = '1' AND AUDIT_FLAG = #{ifComingSoon}
AND (APPROVE_RESULT = '0' OR APPROVE_RESULT = '1')
))) and a.valid = '1' order by a.create_time desc

