SELECT a.STAFF_ID staffId, u.USERNAME, u.USERALIAS, a.DEPT_ID deptId, d.DEPTNAME, (select name from BG_SYS_DATADICTIONARY where pcode ='node_type' and code = a.NODE_TYPE) NODETYPE FROM BG_YY_APPROVE a LEFT JOIN BG_SYS_DEPT d ON a.DEPT_ID = d.DEPTID LEFT JOIN BG_SYS_USER u ON u.USERID = a.STAFF_ID WHERE 1=1
AND a.DEPT_ID = #{deptId}
AND a.NODE_TYPE = #{nodeType}
AND a.ITEM_SECOND_ID = #{itemSecondId}
AND a.VALID = '1' AND d.VALID = '1' AND u.VALID = '1'
